import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./styles.css";

const Dashboard = ({ setIsLoggedIn }) => {
    const navigate = useNavigate();
    const [savedUrls, setSavedUrls] = useState([]);
    const [newUrl, setNewUrl] = useState("");

    useEffect(() => {
        const userId = localStorage.getItem("userId");
        fetch(`http://localhost:8081/url/${userId}`)
            .then((response) => response.json())
            .then((data) => {
                console.log("Fetched URLs:", data); // Log the fetched URLs
                setSavedUrls(data);
            })
            .catch((error) => console.error("Error fetching URLs:", error));
    }, []);

    const handleLogout = () => {
        localStorage.setItem("isLoggedIn", "false");
        setIsLoggedIn(false);
        navigate("/login"); // Redirect to login page
    };

    const handleCheckboxChange = (id) => {
        setSavedUrls((prevUrls) =>
            prevUrls.map((url) =>
                url.url_id === id ? { ...url, selected: !url.selected } : url // Use `url.url_id` for matching
            )
        );
    };

    const handleDelete = () => {
        const selectedIds = savedUrls.filter(url => url.selected).map(url => url.url_id); // Use `url_id`

        // Optional: Send delete requests to the backend if you have a DELETE endpoint
        setSavedUrls((prevUrls) => prevUrls.filter((url) => !url.selected));

        // Send a request to delete the selected URLs if needed
        fetch("http://localhost:8081/url/delete", {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ ids: selectedIds }),
        })
            .then((response) => response.json())
            .then(() => {
                console.log("URLs deleted");
            })
            .catch((error) => {
                console.error("Failed to delete URLs:", error);
            });
    };

    const handleAddUrl = () => {
        if (newUrl.trim() === "") return;

        const userId = localStorage.getItem("userId");

        const urlRequest = {
            url: newUrl,
            userId: userId,
        };

        fetch("http://localhost:8081/url", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(urlRequest),
        })
            .then((res) => res.json())
            .then((newSavedUrl) => {
                setSavedUrls((prevUrls) => [...prevUrls, newSavedUrl]); // Add to state
                setNewUrl(""); // Clear input
            })
            .catch((error) => {
                console.error("Failed to add URL:", error);
            });
    };

    const handleEditUrl = () => {
        const selectedUrls = savedUrls.filter(url => url.selected);
        if (selectedUrls.length === 0) {
            alert("Please select a URL to edit.");
            return;
        }

        selectedUrls.forEach((url) => {
            const updatedUrl = { ...url, url: url.url };
            fetch(`http://localhost:8081/url/${url.url_id}`, { // Use `url.url_id` for updating
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(updatedUrl),
            })
                .then((response) => response.json())
                .then((data) => {
                    analyzeUrlAndUpdateState(data);
                })
                .catch((error) => console.error("Error updating URL:", error));
        });
    };
    const handleRescan = () => {
        // Find all URLs that are selected
        const selectedUrls = savedUrls.filter(url => url.selected);

        if (selectedUrls.length === 0) {
            alert("Please select at least one URL to rescan.");
            return;
        }

        // For each selected URL, call the backend to rescan the URL
        selectedUrls.forEach((url) => {
            fetch(`http://localhost:8081/url/analyze`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(url),
            })
                .then((response) => response.json())
                .then((updatedUrl) => {
                    // Update the state with the new analysis results
                    setSavedUrls((prevUrls) =>
                        prevUrls.map((item) => (item.url_id === updatedUrl.url_id ? updatedUrl : item))
                    );
                })
                .catch((error) => console.error("Error rescanning URL:", error));
        });
    };

    const analyzeUrlAndUpdateState = (url) => {
        fetch(`http://localhost:8081/url/rescan`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(url),
        })
            .then((response) => response.json())
            .then((updatedUrl) => {
                setSavedUrls((prevUrls) =>
                    prevUrls.map((item) => (item.url_id === updatedUrl.url_id ? updatedUrl : item))
                );
            })
            .catch((error) => console.error("Error analyzing URL:", error));
    };

    return (
        <div className="dashboard">
            <header className="header">
                <img src="/images/Headerlogo.png" alt="Commerce Bank Logo" className="logo" />
                <h1>Commerce Bank: URL Checking System</h1>
            </header>

            <div className="logout-container">
                <button className="logout-btn" onClick={handleLogout}>Logout</button>
            </div>

            <h2>Your Saved URLs</h2>
            <input
                type="text"
                placeholder="Enter a new URL"
                value={newUrl}
                onChange={(e) => setNewUrl(e.target.value)}
            />
            <button className="rescan-btn" onClick={handleAddUrl}>Add URL</button>
            <button className="edit-btn" onClick={handleEditUrl}>Edit Selected</button>
            <button className="rescan-btn" onClick={handleRescan}>Rescan Selected</button>
            <button className="delete-btn" onClick={handleDelete} style={{ backgroundColor: "red", color: "white", marginLeft: "10px" }}>
                Delete Selected
            </button>

            <table>
                <thead>
                    <tr>
                        <th>Select</th>
                        <th>URL</th>
                        <th>SSL Certificates</th>
                        <th>Response Code</th>
                        <th>Response Headers</th>
                        <th>SSL Protocol/Cipher Used</th>
                    </tr>
                </thead>
                <tbody>
                    {savedUrls.map((savedUrl) => (
                        <tr key={savedUrl.url_id}>
                            <td>
                                <input
                                    type="checkbox"
                                    checked={savedUrl.selected || false} // Ensure `selected` property is used to check if it's selected
                                    onChange={() => handleCheckboxChange(savedUrl.url_id)} // Use `url_id` to uniquely identify the URL
                                />
                            </td>
                            <td className="table-cell" data-full-text={savedUrl.url} contentEditable>
                                {savedUrl.url}
                            </td>
                            <td className="table-cell" data-full-text={savedUrl.ssl || "N/A"}>
                                {savedUrl.ssl || "N/A"}
                            </td>
                            <td className="table-cell" data-full-text={savedUrl.resCode || "N/A"}>
                                {savedUrl.resCode || "N/A"}
                            </td>
                            <td className="table-cell" data-full-text={savedUrl.resHead || "N/A"}>
                                {savedUrl.resHead || "N/A"}
                            </td>
                            <td className="table-cell" data-full-text={savedUrl.sslPro || "N/A"}>
                                {savedUrl.sslPro || "N/A"}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;
