import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Dashboard from "./Dashboard";
import LoginPage from "./LoginPage";
import "./styles.css"; // Import CSS

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const userStatus = localStorage.getItem("isLoggedIn");
        setIsLoggedIn(userStatus === "true");
    }, []);

    return (
        <Router>
            <div className="container">
                <header className="header">
                    <div className="header-content">
                        <img src="/images/Headerlogo.png" alt="Commerce Bank Logo" className="logo" />
                    </div>
                </header>

                {/* Add a check to show the correct route based on login status */}
                <Routes>
                    <Route
                        path="/"
                        element={<LoginPage setIsLoggedIn={setIsLoggedIn} />}
                    />
                    <Route
                        path="/dashboard"
                        element={isLoggedIn ? <Dashboard setIsLoggedIn={setIsLoggedIn} /> : <LoginPage setIsLoggedIn={setIsLoggedIn} />}
                    />
                </Routes>
            </div>
        </Router>
    );
};

