import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Dashboard from "./Dashboard";
import LoginPage from "./LoginPage";
import "./styles.css"; // Import CSS

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const userStatus = localStorage.getItem("isLoggedIn");
        setIsLoggedIn(userStatus === "true");
    }, []);

    return (
        <Router>
            <div className="container">
            <header className="header">
  <div className="header-content">
    <img src="/images/Headerlogo.png" alt="Commerce Bank Logo" className="logo" />
  </div>

  {/* Add these lines here to show "Logged in as" and Logout */}
  {localStorage.getItem("isLoggedIn") === "true" && window.location.pathname === "/dashboard" && (
    <div className="header-user-info">
      <span>Logged in as: {localStorage.getItem("userId")}</span>
      <button
        className="logout-btn"
        onClick={() => {
          localStorage.clear();
          window.location.href = "/login";
        }}
        style={{ backgroundColor: "red", color: "white", marginLeft: "10px" }}>
        Logout
      </button>
    </div>
  )}
</header>
                  {/* FDIC Sub-header */}
        <div className="sub-header">
          <span className="fdic-logo">FDIC</span>
          <span className="fdic-text">
            FDIC-Insured â€“ Backed by the full faith and credit of the U.S. Government
          </span>
        </div>

                {/* Add a check to show the correct route based on login status */}
                        {/* Routing */}
        <Routes>
          <Route path="/" element={<LoginPage setIsLoggedIn={setIsLoggedIn} />} />
          <Route path="/dashboard" element={<Dashboard setIsLoggedIn={setIsLoggedIn} />} />
          <Route path="/login" element={<LoginPage setIsLoggedIn={setIsLoggedIn} />} />
        </Routes>
      </div>
    </Router>
    );
};

export default App;
