import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import Dashboard from "./Dashboard";
import LoginPage from "./LoginPage";
import "./styles.css"; // Import external CSS styling

// Main App component
const App = () => {
  // State to track if the user is logged in, initialized based on localStorage
  const [isLoggedIn, setIsLoggedIn] = useState(() => {
    return localStorage.getItem("isLoggedIn") === "true";
  });

  // Hook to get the current URL path
  const location = useLocation();

  return (
    <div className="container">
      {/* Top Header Section */}
      <header className="header">
        {/* Logo inside the header */}
        <div className="header-content">
          <img src="/images/Headerlogo.png" alt="Commerce Bank Logo" className="logo" />
        </div>

        {/* If logged in and currently on Dashboard page, show user info and Logout button */}
        {isLoggedIn && location.pathname === "/dashboard" && (
          <div className="header-user-info">
            <span>Logged in as: {localStorage.getItem("userId")}</span>
            <button
              className="logout-btn"
              onClick={() => {
                // Clear local storage, set user as logged out, and redirect to login page
                localStorage.clear();
                setIsLoggedIn(false);
                window.location.href = "/login";
              }}
            >
              Logout
            </button>
          </div>
        )}
      </header>

      {/* FDIC Sub-header section */}
      <div className="sub-header">
        <span className="fdic-logo">FDIC</span>
        <span className="fdic-text">
          FDIC-Insured â€“ Backed by the full faith and credit of the U.S. Government
        </span>
      </div>

      {/* Main App Routes */}
      <Routes>
        {/* Route for Home ("/") -> Login Page */}
        <Route path="/" element={<LoginPage setIsLoggedIn={setIsLoggedIn} />} />

        {/* Route for Dashboard */}
        <Route path="/dashboard" element={<Dashboard setIsLoggedIn={setIsLoggedIn} />} />

        {/* Route for Login explicitly */}
        <Route path="/login" element={<LoginPage setIsLoggedIn={setIsLoggedIn} />} />
      </Routes>
    </div>
  );
};

// AppWrapper wraps App inside <Router>
// This is needed because App uses useLocation, which requires being inside a Router
const AppWrapper = () => (
  <Router>
    <App />
  </Router>
);

export default AppWrapper;
