import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from "react-router-dom";
import Dashboard from "./Dashboard";
import LoginPage from "./LoginPage";
import "./styles.css";

const Header = ({ isLoggedIn, handleLogout }) => {
  const location = useLocation();
  const userId = localStorage.getItem("userId");

  return (
    <header className="header">
      <div className="header-content">
        <img src="/images/Headerlogo.png" alt="Commerce Bank Logo" className="logo" />
      </div>
      {isLoggedIn && location.pathname === "/dashboard" && (
        <div className="header-user-info">
          <span>Logged in as: {userId}</span>
          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        </div>
      )}
    </header>
  );
};

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogout = () => {
    localStorage.clear();
    setIsLoggedIn(false);
    window.location.href = "/login"; // force redirect after logout
  };

  useEffect(() => {
    const userStatus = localStorage.getItem("isLoggedIn");
    setIsLoggedIn(userStatus === "true");
  }, []);

  return (
    <Router>
      <div className="container">
        <Header isLoggedIn={isLoggedIn} handleLogout={handleLogout} />

        {/* Sub-header */}
        <div className="sub-header">
          <span className="fdic-logo">FDIC</span>
          <span className="fdic-text">
            FDIC-Insured â€“ Backed by the full faith and credit of the U.S. Government
          </span>
        </div>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<LoginPage setIsLoggedIn={setIsLoggedIn} />} />
          <Route path="/dashboard" element={<Dashboard setIsLoggedIn={setIsLoggedIn} />} />
          <Route path="/login" element={<LoginPage setIsLoggedIn={setIsLoggedIn} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
