import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./styles.css";

const Dashboard = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();
  const [savedUrls, setSavedUrls] = useState([]);
  const [newUrl, setNewUrl] = useState("");

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    fetch(`http://localhost:8081/urls/${userId}`)
      .then((response) => response.json())
      .then((data) => setSavedUrls(data))
      .catch((error) => console.error("Error fetching URLs:", error));
  }, [navigate]);

  const handleLogout = () => {
    localStorage.setItem("isLoggedIn", "false");
    setIsLoggedIn(false);
    navigate("/login");
  };

  const handleCheckboxChange = (id) => {
    setSavedUrls((prevUrls) =>
      prevUrls.map((url) =>
        url.url_id === id ? { ...url, selected: !url.selected } : url
      )
    );
  };

  const handleDelete = () => {
    const selectedIds = savedUrls.filter(url => url.selected).map(url => url.url_id);

    if (selectedIds.length === 0) {
      alert("Please select at least one URL to delete.");
      return;
    }

    fetch("http://localhost:8081/urls", {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(selectedIds),
    })
      .then((response) => response.json())
      .then((updatedUrls) => {
        setSavedUrls(updatedUrls);
        alert("Selected URLs deleted.");
      })
      .catch((error) => console.error("Error deleting URLs:", error));
  };

  const handleAddUrl = () => {
    if (newUrl.trim() === "") return;

    const userId = localStorage.getItem("userId");

    fetch("http://localhost:8081/url", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: "User URL", url: newUrl, userId }),
    })
      .then((res) => res.json())
      .then((newSavedUrl) => {
        setSavedUrls((prevUrls) => [...prevUrls, newSavedUrl]);
        setNewUrl("");
      })
      .catch((error) => console.error("Failed to add URL:", error));
  };

  const handleEditSelected = () => {
    const updatedUrls = savedUrls.filter(url => url.selected).map(url => {
      const updatedUrl = document.getElementById(`url-${url.url_id}`).innerText;
      return { ...url, url: updatedUrl };
    });

    if (updatedUrls.length === 0) {
      alert("Please select at least one URL to edit.");
      return;
    }

    fetch("http://localhost:8081/urls", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedUrls),
    })
      .then((response) => response.json())
      .then((responseData) => {
        setSavedUrls((prevUrls) =>
          prevUrls.map((url) => {
            const updatedUrl = responseData.find((u) => u.url_id === url.url_id);
            return updatedUrl ? updatedUrl : url;
          })
        );
      })
      .catch((error) => console.error("Error updating URLs:", error));
  };

  const handleRescan = () => {
    const selectedIds = savedUrls.filter(url => url.selected).map(url => url.url_id);

    if (selectedIds.length === 0) {
      alert("Please select at least one URL to rescan.");
      return;
    }

    fetch("http://localhost:8081/rescan", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(selectedIds),
    })
      .then((response) => response.json())
      .then((updatedUrls) => {
        if (Array.isArray(updatedUrls)) {
          setSavedUrls((prevUrls) =>
            prevUrls.map((url) => {
              const updatedUrl = updatedUrls.find((u) => u.url_id === url.url_id);
              return updatedUrl ? updatedUrl : url;
            })
          );
        } else {
          console.error("Unexpected response format:", updatedUrls);
        }
      })
      .catch((error) => console.error("Error rescanning URL:", error));
  };

  return (
    <div className="dashboard"> {/* âœ… Parent div wrapper */}
      <header className="header dashboard-header">
        <div className="left-section">
          <img src="/images/Headerlogo.png" alt="Commerce Bank Logo" className="logo" />
        </div>
        <div className="right-section">
          <span className="user-info">Logged in as: {localStorage.getItem("userId")}</span>
          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        </div>
      </header>

      <h2>Your Saved URLs</h2>

      <div className="actions-container">
        <input
          type="text"
          placeholder="Enter a new URL"
          value={newUrl}
          onChange={(e) => setNewUrl(e.target.value)}
        />
        <button className="rescan-btn" onClick={handleAddUrl}>Add URL</button>
        <button className="edit-btn" onClick={handleEditSelected}>Edit Selected</button>
        <button className="rescan-btn" onClick={handleRescan}>Rescan Selected</button>
        <button className="delete-btn" onClick={handleDelete}>Delete Selected</button>
      </div>

      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>URL</th>
            <th>SSL Certificates</th>
            <th>Response Code</th>
            <th>Response Headers</th>
            <th>SSL Protocol/Cipher Used</th>
          </tr>
        </thead>
        <tbody>
          {savedUrls.map((savedUrl) => (
            <tr key={savedUrl.url_id}>
              <td>
                <input
                  type="checkbox"
                  checked={savedUrl.selected || false}
                  onChange={() => handleCheckboxChange(savedUrl.url_id)}
                />
              </td>
              <td
                id={`url-${savedUrl.url_id}`}
                className="table-cell"
                data-full-text={savedUrl.url}
                contentEditable
              >
                {savedUrl.url}
              </td>
              <td className="table-cell">{savedUrl.ssl || "N/A"}</td>
              <td className="table-cell">{savedUrl.resCode || "N/A"}</td>
              <td className="table-cell">{savedUrl.resHead || "N/A"}</td>
              <td className="table-cell">{savedUrl.sslPro || "N/A"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
