import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./styles.css";

const Dashboard = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();
  const [savedUrls, setSavedUrls] = useState([]);
  const [newUrl, setNewUrl] = useState("");

  useEffect(() => {
    setSavedUrls([
      { id: 1, url: "https://example.com", status: "OK", ssl: "Valid", responseCode: 200, headers: "Content-Type: text/html", cipher: "TLS 1.2", selected: false },
      { id: 2, url: "https://google.com", status: "OK", ssl: "Valid", responseCode: 200, headers: "Content-Type: text/html", cipher: "TLS 1.3", selected: false },
    ]);
  }, []);

  const handleLogout = () => {
    localStorage.setItem("isLoggedIn", "false");
    setIsLoggedIn(false);
    navigate("/login"); // Redirect to login page
  };

  //  Toggle selection state for checkboxes
  const handleCheckboxChange = (id) => {
    setSavedUrls((prevUrls) =>
      prevUrls.map((url) =>
        url.id === id ? { ...url, selected: !url.selected } : url
      )
    );
  };

  //  Delete only selected URLs
  const handleDelete = () => {
    setSavedUrls((prevUrls) => prevUrls.filter((url) => !url.selected));
  };

  const handleAddUrl = () => {
    if (newUrl.trim() === "") return;

    const newEntry = {
      id: savedUrls.length + 1,
      url: newUrl,
      status: "Unknown",
      ssl: "Checking...",
      responseCode: "N/A",
      headers: "N/A",
      cipher: "N/A",
      selected: false,
    };

    setSavedUrls((prevUrls) => [...prevUrls, newEntry]);
    setNewUrl("");
  };

  return (
    <div className="dashboard">
      {/* Header */}
      <header className="header">
        <img src="/images/Headerlogo.png" alt="Commerce Bank Logo" className="logo" />
        <h1>Commerce Bank: URL Checking System</h1>
      </header>

      {/* Logout Button */}
      <div className="logout-container">
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* URL Management Section */}
      <h2>Your Saved URLs</h2>
      <input
        type="text"
        placeholder="Enter a new URL"
        value={newUrl}
        onChange={(e) => setNewUrl(e.target.value)} 
      />
      <button className="rescan-btn" onClick={handleAddUrl}>
        Add URL
      </button>
      <button className="edit-btn">Edit Selected</button>
      <button className="rescan-btn">Rescan Selected</button>
      <button className="delete-btn" onClick={handleDelete} style={{ backgroundColor: "red", color: "white", marginLeft: "10px" }}>
        Delete Selected
      </button>

      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>URL</th>
            <th>SSL Certificates</th>
            <th>Response Code</th>
            <th>Response Headers</th>
            <th>SSL Protocol/Cipher Used</th>
          </tr>
        </thead>
        <tbody>
          {savedUrls.map((savedUrl) => (
            <tr key={savedUrl.id}>
              <td>
                <input
                  type="checkbox"
                  checked={savedUrl.selected || false}
                  onChange={() => handleCheckboxChange(savedUrl.id)}
                />
              </td>
              <td contentEditable>{savedUrl.url}</td>
              <td>{savedUrl.ssl}</td>
              <td>{savedUrl.responseCode}</td>
              <td>{savedUrl.headers}</td>
              <td>{savedUrl.cipher}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
