import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./styles.css";

const Dashboard = ({ setIsLoggedIn }) => {
    const navigate = useNavigate();
    const [savedUrls, setSavedUrls] = useState([]);
    const [newUrl, setNewUrl] = useState("");

    useEffect(() => {
        const userId = localStorage.getItem("userId");
        fetch(`http://localhost:8081/url/${userId}`)
            .then((response) => response.json())
            .then((data) => {
                console.log("Fetched URLs:", data); // Log the fetched URLs
                setSavedUrls(data);
            })
            .catch((error) => console.error("Error fetching URLs:", error));
    }, []);

    const handleLogout = () => {
        localStorage.setItem("isLoggedIn", "false");
        setIsLoggedIn(false);
        navigate("/login"); // Redirect to login page
    };

    const handleCheckboxChange = (id) => {
        setSavedUrls((prevUrls) =>
            prevUrls.map((url) =>
                url.id === id ? { ...url, selected: !url.selected } : url
            )
        );
    };

    const handleDelete = () => {
        const selectedIds = savedUrls.filter(url => url.selected).map(url => url.id);

        // Optional: Send delete requests to the backend if you have a DELETE endpoint
        setSavedUrls((prevUrls) => prevUrls.filter((url) => !url.selected));

        // Send a request to delete the selected URLs if needed
        fetch("http://localhost:8081/url/delete", {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ ids: selectedIds }),
        })
            .then((response) => response.json())
            .then(() => {
                console.log("URLs deleted");
            })
            .catch((error) => {
                console.error("Failed to delete URLs:", error);
            });
    };

    const handleAddUrl = () => {
        if (newUrl.trim() === "") return;

        const userId = localStorage.getItem("userId");

        const urlRequest = {
            url: newUrl,
            userId: userId,
        };

        fetch("http://localhost:8081/url", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(urlRequest),
        })
            .then((res) => res.json())
            .then((newSavedUrl) => {
                setSavedUrls((prevUrls) => [...prevUrls, newSavedUrl]); // Add to state
                setNewUrl(""); // Clear input
            })
            .catch((error) => {
                console.error("Failed to add URL:", error);
            });
    };

    return (
        <div className="dashboard">
            {/* Header */}
            <header className="header">
                <img src="/images/Headerlogo.png" alt="Commerce Bank Logo" className="logo" />
                <h1>Commerce Bank: URL Checking System</h1>
            </header>

            {/* Logout Button */}
            <div className="logout-container">
                <button className="logout-btn" onClick={handleLogout}>
                    Logout
                </button>
            </div>

            {/* URL Management Section */}
            <h2>Your Saved URLs</h2>
            <input
                type="text"
                placeholder="Enter a new URL"
                value={newUrl}
                onChange={(e) => setNewUrl(e.target.value)}
            />
            <button className="rescan-btn" onClick={handleAddUrl}>
                Add URL
            </button>
            <button className="edit-btn">Edit Selected</button>
            <button className="rescan-btn">Rescan Selected</button>
            <button className="delete-btn" onClick={handleDelete} style={{ backgroundColor: "red", color: "white", marginLeft: "10px" }}>
                Delete Selected
            </button>

            <table>
                <thead>
                    <tr>
                        <th>Select</th>
                        <th>URL</th>
                        <th>SSL Certificates</th>
                        <th>Response Code</th>
                        <th>Response Headers</th>
                        <th>SSL Protocol/Cipher Used</th>
                    </tr>
                </thead>
                <tbody>
                    {savedUrls.map((savedUrl) => (
                        <tr key={savedUrl.id}>
                            <td>
                                <input
                                    type="checkbox"
                                    checked={savedUrl.selected || false}
                                    onChange={() => handleCheckboxChange(savedUrl.id)}
                                />
                            </td>
                            <td contentEditable>{savedUrl.url}</td>
                            <td>{savedUrl.ssl}</td>
                            <td>{savedUrl.resCode}</td>
                            <td>{savedUrl.resHead}</td>
                            <td>{savedUrl.sslPro}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;
